import React, { useState } from 'react';
import '../style/TaskList.css';


// Componente principal da lista de tarefas
const ListaDeTarefas = () => {
  // Estado para armazenar as tarefas
  const [tarefas, setTarefas] = useState([]);
  
  // Estado para armazenar a nova tarefa a ser adicionada
  const [novaTarefa, setNovaTarefa] = useState('');
  
  // Estado para controlar o filtro de tarefas (todas, concluídas, pendentes)
  const [mostrarConcluidas, setMostrarConcluidas] = useState('todas');
  
  // Estado para armazenar o horário da nova tarefa
  const [horarioTarefa, setHorarioTarefa] = useState('');
  
  // Estado para armazenar o tipo da nova tarefa (fazer, planejar, delegar, eliminar)
  const [tipoTarefa, setTipoTarefa] = useState('eliminar');
  
  // Estado para armazenar o índice da tarefa que está sendo editada
  const [editarTarefaIndex, setEditarTarefaIndex] = useState(null);
  
  // Estado para armazenar os dados da tarefa sendo editada
  const [tarefaEditada, setTarefaEditada] = useState('');
  const [horarioEditado, setHorarioEditado] = useState('');
  const [tipoEditado, setTipoEditado] = useState('eliminar');

  // Função para adicionar uma nova tarefa
  const adicionarTarefa = () => {
    if (novaTarefa.trim() !== '') { // Verifica se o nome da tarefa não está vazio
      const nova = {
        nome: novaTarefa,
        concluida: false,
        horario: horarioTarefa || 'Sem horário',
        tipo: tipoTarefa,
      };
      
      // Adiciona a nova tarefa e ordena por horário
      const tarefasAtualizadas = [...tarefas, nova].sort((a, b) =>
        a.horario > b.horario ? 1 : -1
      );
      
      setTarefas(tarefasAtualizadas); // Atualiza o estado com a nova lista de tarefas
      setNovaTarefa(''); // Limpa o campo de nova tarefa
      setHorarioTarefa(''); // Limpa o campo de horário
      setTipoTarefa('eliminar'); // Reseta o tipo da tarefa
    }
  };

  // Função para alternar o status de conclusão de uma tarefa
  const alternarTarefa = (indice) => {
    const tarefasAtualizadas = tarefas.map((tarefa, i) =>
      i === indice ? { ...tarefa, concluida: !tarefa.concluida } : tarefa
    );
    setTarefas(tarefasAtualizadas); // Atualiza o estado com a lista de tarefas alterada
  };

  // Função para iniciar a edição de uma tarefa
  const iniciarEdicao = (indice) => {
    setEditarTarefaIndex(indice); // Define o índice da tarefa sendo editada
    setTarefaEditada(tarefas[indice].nome); // Preenche o campo de edição com o nome da tarefa
    setHorarioEditado(tarefas[indice].horario); // Preenche o campo de edição com o horário da tarefa
    setTipoEditado(tarefas[indice].tipo); // Preenche o campo de edição com o tipo da tarefa
  };

  // Função para salvar a edição da tarefa
  const salvarEdicao = () => {
    const tarefasAtualizadas = tarefas.map((tarefa, i) =>
      i === editarTarefaIndex
        ? { ...tarefa, nome: tarefaEditada, horario: horarioEditado, tipo: tipoEditado }
        : tarefa
    );
    
    // Ordena as tarefas novamente por horário
    setTarefas(tarefasAtualizadas.sort((a, b) =>
      a.horario > b.horario ? 1 : -1
    ));
    
    // Limpa os estados de edição
    setEditarTarefaIndex(null);
    setTarefaEditada('');
    setHorarioEditado('');
    setTipoEditado('eliminar');
  };

  // Função para excluir uma tarefa
  const excluirTarefa = (indice) => {
    const tarefasAtualizadas = tarefas.filter((_, i) => i !== indice);
    setTarefas(tarefasAtualizadas); // Atualiza a lista de tarefas sem a tarefa excluída
    setEditarTarefaIndex(null); // Fechar a aba de edição automaticamente
  };

  // Filtra as tarefas com base no estado de mostrarConcluidas
  const tarefasFiltradas = mostrarConcluidas === 'concluidas'
    ? tarefas.filter(tarefa => tarefa.concluida)
    : mostrarConcluidas === 'pendentes'
      ? tarefas.filter(tarefa => !tarefa.concluida)
      : tarefas;

  return (
    <div className="container-lista-tarefas">
      <h1>Lista de Tarefas</h1>
      
      {/* Campo para digitar uma nova tarefa */}
      <input
        type="text"
        placeholder="Digite uma nova tarefa"
        value={novaTarefa}
        onChange={(e) => setNovaTarefa(e.target.value)}
        className="input-tarefa"
      />
      
      {/* Linha para opções de horário, prioridade e filtros */}
      <div className="opcoes-tarefa">
        <input
          type="time"
          value={horarioTarefa}
          onChange={(e) => setHorarioTarefa(e.target.value)}
          className="input-horario"
        />
        <select
          value={tipoTarefa}
          onChange={(e) => setTipoTarefa(e.target.value)}
          className="input-tipo"
        >
          <option value="fazer">Fazer</option>
          <option value="planejar">Planejar</option>
          <option value="delegar">Delegar</option>
          <option value="eliminar">Eliminar</option>
        </select>
        <select
          value={mostrarConcluidas}
          onChange={(e) => setMostrarConcluidas(e.target.value)}
          className="filtro-tarefas"
        >
          <option value="todas">Todas</option>
          <option value="concluidas">Concluídas</option>
          <option value="pendentes">Pendentes</option>
        </select>
      </div>
      
      {/* Botão para adicionar a tarefa */}
      <button onClick={adicionarTarefa} className="botao-adicionar">Adicionar</button>
      
      {/* Lista de tarefas filtradas */}
      <ul className="lista-tarefas">
        {tarefasFiltradas.map((tarefa, indice) => (
          <li key={indice} className={`tarefa ${tarefa.tipo} ${tarefa.concluida ? 'concluida' : ''}`}>
            <span>{indice + 1}. {tarefa.nome}</span>
            <div className="detalhes-tarefa">
              <span className="horario">{tarefa.horario}</span>
              <button onClick={() => alternarTarefa(indice)} className="botao-concluir"></button>
              <button onClick={() => iniciarEdicao(indice)} className="botao-editar">Editar</button>
            </div>
          </li>
        ))}
      </ul>

      {/* Modal para edição de tarefa */}
      {editarTarefaIndex !== null && (
        <div className="modal-editar">
          <div className="modal-conteudo">
            <h3>Editar Tarefa</h3>
            
            {/* Campo para editar o nome da tarefa */}
            <input
              type="text"
              value={tarefaEditada}
              onChange={(e) => setTarefaEditada(e.target.value)}
              className="input-tarefa"
            />
            
            {/* Campo para editar o horário da tarefa */}
            <input
              type="time"
              value={horarioEditado}
              onChange={(e) => setHorarioEditado(e.target.value)}
              className="input-horario"
            />
            
            {/* Campo para editar o tipo da tarefa */}
            <select
              value={tipoEditado}
              onChange={(e) => setTipoEditado(e.target.value)}
              className="input-tipo"
            >
              <option value="fazer">Fazer</option>
              <option value="planejar">Planejar</option>
              <option value="delegar">Delegar</option>
              <option value="eliminar">Eliminar</option>
            </select>
            
            {/* Botões de ação no modal de edição */}
            <div className="acoes-editar">
              <button onClick={salvarEdicao} className="botao-salvar">Salvar</button>
              <button onClick={() => excluirTarefa(editarTarefaIndex)} className="botao-excluir">Excluir</button>
              <button onClick={() => setEditarTarefaIndex(null)} className="botao-cancelar">Cancelar</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ListaDeTarefas;
